  mounted() {
    this.pointinit();
    // console.log('i18n------->',this.$i18n.locale)

    // 获取菜单和菜单底部的横线元素
    const menu = document.querySelector(".nav ");
    const menuLine = document.querySelector(".menu-line");

    // 创建GSAP动画
    gsap.to(menuLine, {
      duration: 0.3,
      scaleX: 0,
      transformOrigin: "right",
      ease: "power1.inOut",
    });

    menu.addEventListener("mousemove", (e) => {
      // 获取鼠标在菜单中的位置
      const xPos = e.clientX - menu.offsetLeft;

      // 更新菜单底部横线的位置和宽度
      gsap.to(menuLine, {
        duration: 0.3,
        scaleX: 1,
        left: xPos,
        transformOrigin: "left",
        ease: "power1.inOut",
      });
    });

    menu.addEventListener("mouseleave", () => {
      // 鼠标离开菜单后，隐藏菜单底部横线
      gsap.to(menuLine, {
        duration: 0.3,
        scaleX: 0,
        transformOrigin: "right",
        ease: "power1.inOut",
      });
    });
    this.$nextTick(() => {
      if (process.browser) {
        new WOW({
          //可以添加自定义内容
          animateClass: "animate__animated",
          offset: 200,
        }).init();
      }
      this.taginits();
    });
  },
  methods: {
    pointinit() {
      // mousemove ball
      // 指针小球跟随效果
      gsap.set(".ball", { xPercent: -65, yPercent: -65 });
      const ball = document.querySelector(".ball");
      const pos = { x: window.innerWidth / 2, y: window.innerHeight / 2 };
      const mouse = { x: pos.x, y: pos.y };
      const speed = 0.35;
      const xSet = gsap.quickSetter(ball, "x", "px");
      const ySet = gsap.quickSetter(ball, "y", "px");

      window.addEventListener("mousemove", (e) => {
        mouse.x = e.x;
        mouse.y = e.y;
      });
      gsap.ticker.add(() => {
        // adjust speed for higher refresh monitors
        const dt = 1.0 - Math.pow(1.0 - speed, gsap.ticker.deltaRatio());
        pos.x += (mouse.x - pos.x) * dt;
        pos.y += (mouse.y - pos.y) * dt;
        xSet(pos.x);
        ySet(pos.y);
      });
      // 指针小球跟随效果 end
      // 指针小球hover效果  **
      const ahrefbtn = document.querySelectorAll("a");
      ahrefbtn.forEach((a) => {
        a.addEventListener("pointerenter", handleMouseEnter);
        a.addEventListener("pointerleave", handleMouseLeave);
      });
      function handleMouseEnter() {
        gsap.to(".ball", {
          duration: 0.3,
          scale: 3,
          opacity: 0,
          ease: "Power3.easeOut",
        });
      }
      function handleMouseLeave() {
        gsap.to(".ball", {
          duration: 0.3,
          scale: 1,
          opacity: 1,
          ease: "Power3.easeOut",
        });
      }
      // 指针小球hover效果 end **
      // mousemove ball end
    },

    handleSelect(index, indexPath) {
      // console.log("===", index, indexPath);
      this.actIndex = index;
    },
    handleLang(_type) {
      console.log("当前的语言类型： --->", this.$i18n.locale);
      if (this.$i18n.locale === _type) return;
      console.log("点击的语言类型： --->", _type);
      // console.log('路径打印',this.switchLocalePath(_type))
      let _path = this.switchLocalePath(_type);
      this.$router.push(_path);
      var _text = "";
      if (_type === "cn") {
        _text = "微软雅黑";
      } else _text = "Noto Sans HK";
      console.log("准备设置的字体： ===>", _text);
      // console.log(document.documentElement.style)
      document.documentElement.style.setProperty("--font_family", _text); //var(--font_family)
    },
    taginits() {
